import React from "react"
import * as ReactDOMClient from "react-dom/client"

class Header extends React.Component{
  render() {
    return <h3>{this.props.title}</h3>
  }
}

class Button extends React.Component{
  render() {
    return (
      <button onClick={this.props.action}>{this.props.text}</button>
    )
  }
}

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      name: null,
      changedName: null
    }

    this.save = this.save.bind(this)
  }

  render() {
    return (
      <div className="name">
        <Header title = {this.state.name} />
        <label htmlFor="nickName">ФИО</label>
        <input 
          id="nickName" 
          placeholder={"Введите имя"} 
          onChange={(e) => this.setState({changedName: e.target.value})}
        />
        <Button 
          text="Сохранить" 
          action={this.save(this.changedName)} 
        />
      </div>)
  }

  save(changedName) { this.setState({name: changedName}) }
}

const app = ReactDOMClient.createRoot(document.getElementById("app"))
app.render(<App />)
