import React, { useState } from "react"
import Button from "./Components/Button"
import Header from "./Components/Header"


const App = (props) => {
  const [name, changeName] = useState("")
  const [newName, setName] = useState("")

  const saveName = () => { setState({name: this.state.changedName}) }
  const resetName = () => { this.setState({name: ""}) }

  return (
      <div className="name">
        <Header title = {this.state.name} />
        <main>
            <label htmlFor="nickName">Имя</label><br />
            <input 
            id="nickName" 
            placeholder={"Введите имя"} 
            onChange={(e) => this.setState({changedName: e.target.value})}
            /><br />
            <Button 
            text="Сохранить" 
            className="save"
            action={this.saveName} 
            />
            <Button 
            text="Сбросить"
            className="reset"
            action={this.resetName}
            />
        </main>
      </div>)
      

}

export default App
