import React from "react"
import * as ReactDOMClient from "react-dom/client"
import "./main.css"
import Header from "./Header"

class Button extends React.Component{
  render() {
    return (
      <button onClick={this.props.action}>{this.props.text}</button>
    )
  }
}

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      name: "",
      changedName: ""
    }

    this.saveName = this.saveName.bind(this)
    this.resetName = this.resetName.bind(this)
  }

  render() {
    return (
      <div className="name">
        <Header title = {this.state.name} />
        <label htmlFor="nickName">Имя</label>
        <input 
          id="nickName" 
          placeholder={"Введите имя"} 
          onChange={(e) => this.setState({changedName: e.target.value})}
        />
        <Button 
          text="Сохранить" 
          action={this.saveName} 
        />
        <Button 
          text="Сбросить"
          action={this.resetName}
        />
      </div>)
  }

  saveName() { this.setState({name: this.state.changedName}) }
  resetName() { this.setState({name: ""}) }
}

const app = ReactDOMClient.createRoot(document.getElementById("app"))
app.render(<App />)
